import React from 'react';
import { connect } from 'react-redux';
import {signIn, signOut } from '../../store/actions/googleAuthActions';
import ModalRegister from '../../components/modal/ModalRegister';
import Divider from '@material-ui/core/Divider';
import MenuItem from '@material-ui/core/MenuItem';


class GoogleAuth extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          isOpen: false
        };
        this.toggle = this.toggle.bind(this);
      }

      toggle() {
        this.setState({
          isOpen: !this.state.isOpen
        });
      }

    componentDidMount() {
        window.gapi.load('client:auth2', () => {
            window.gapi.client
            .init({
                clientId: '162564132974-al7mv7822j2b88bqka4ps7q29vn4quk3.apps.googleusercontent.com',
                scope: 'email'
            })
            .then(() => {
                this.auth = window.gapi.auth2.getAuthInstance();
                this.onAuthChange(this.auth.isSignedIn.get());
                this.auth.isSignedIn.listen(this.onAuthChange);
                console.log(this.auth.currentUser.get().getId())
            });
        });
    }

    onAuthChange = isSignedIn => {
        if(isSignedIn) {
            this.props.signIn(this.auth.currentUser.get().getId());
        } else {
            this.props.signOut();
        }
    };

    onSignInClick = () => {
        this.auth.signIn();
    };
    onSignOutClick = () => {
        this.auth.signOut();
        window.location.reload(); 
    };


    renderAuthButton() {
        const style= {
            signout: {
                color: 'red',
            },
        }
        if(this.props.isSignedIn === null) {
            return null;
        } else if (this.props.isSignedIn) {
            return (
                <div 
                style={style.signout}
                onClick={this.onSignOutClick}>
                    Sign Out
                </div>
            )
        } else {
            return (
                <div
                onClick={this.onSignInClick}>
                    Sign In

                </div>
            )
        }
    }
    ModalRegister = () => {

    }

    render () {
        return (
            <div>
                <MenuItem>
                    {this.renderAuthButton()}
                </MenuItem>
                <Divider />
                <MenuItem>
                    Register
                </MenuItem>
                <ModalRegister toggle={toggle} />
            </div>
        )
    }
}

    const mapStateToProps = (state) => {
    return { isSignedIn: state.auth.isSignedIn };
};

export default connect(mapStateToProps, { signIn, signOut }
    )(GoogleAuth);
