import { SIGN_IN, SIGN_OUT } from '../actions/types';

const INITIAL_STATE = {
    isSignedIn: null,
    userId: null,
    isAdmin: null
};

export default(state = INITIAL_STATE, action) => {
    switch(action.type) {
        case SIGN_IN:
            return {
                ...state, 
                isSignedIn: true, 
                userId: action.payload, 
                isAdmin: action.isAdmin 
            };
        case SIGN_OUT:
            return {
                ...state, 
                isSignedIn: false, 
                userId: null 
            };
        default:
            return state;
    }
};
