import axios from 'axios';
import { ON_SIGN_IN, FETCH_RESERVATIONS, DELETE_RESERVATION, MAIL_RESERVATION } from './types';



export const onSignIn = (token) => dispatch => {
  // Post request to backend
  fetch('https://www.thecleanersisrael.com/api/account/signin', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      email: signInEmail,
      password: signInPassword,
    }),
  }).then(res => res.json())
    .then(json => {
      console.log('json', json);
      if (json.success) {
        dispatch({
          type: ON_SIGN_IN,
          payload: json,
        });
        this.setState({
          signInError: json.message,
          isLoading: false,
          signInPassword: '',
          signInEmail: '',
          token: json.token,
        });
      } else {
        this.setState({
          signInError: json.message,
          isLoading: false,
        });
      }
    });
  }
