import { LOCALE_SET } from './types';

export const localeSet = lang => ({
    type: LOCALE_SET,
    lang
})

export const setLocale = (lang, req) => dispatch => {
    localStorage.alhubLang = lang;
    dispatch(localeSet(lang));
    if(lang === 'fr') 
        return alert(req)
    if(lang ==='he') 
        return alert('heeb')

}
