import { SIGN_IN, SIGN_OUT, FILL_INFO } from '../actions/types';

const INITIAL_STATE = {
    isSignedIn: null,
    userId: null,
    isAdmin: null,
    name: '',
    surname: '',
    tz: '',
    address: '',
    phone: '',
    mail: ''

};

export default(state = INITIAL_STATE, action) => {
    switch(action.type) {
        case SIGN_IN:
            return {
                ...state, 
                isSignedIn: true, 
                userId: action.payload, 
                isAdmin: action.isAdmin 
            };
        case SIGN_OUT:
            return {
                ...state, 
                isSignedIn: false, 
                userId: null 
            };
            case FILL_INFO:
            return {
                ...state, 
                name: action.payload, 
                surname: action.payload,
                tz: action.payload,
                address: action.payload,
                phone: action.payload,
                mail: action.payload
            };
        default:
            return state;
    }
};
