import React, { Component } from 'react'
import { getFromStorage, setInStorage } from '../../config/storage';
export default class login extends Component {
    constructor(props) {
        super(props);

        this.state = {
            isLoading: true,
            token: '',
            signUpError: '',
            signInError: '',
            signInEmail: '',
            signInPassword: '',
            signUpFirstName: '',
            signUpLastName: '',
            signUpEmail: '',
            signUpPassword: ''
        };
    }

    componentDidMount() {
        const obj = getFromStorage('the_main_app');
        if (obj && obj.token) {
          const { token } = obj;
          // Verify token
          fetch('/api/account/verify?token=' + token)
            .then(res => res.json())
            .then(json => {
              if (json.success) {
                this.setState({
                  token,
                  isLoading: false
                });
              } else {
                this.setState({
                  isLoading: false,
                });
              }
            });
        } else {
          this.setState({
            isLoading: false,
          });
        }
      }

      onTextboxChangeSignInEmail(e) {
          this.setState({
              signInEmail: e.target.value,
          })
      }
      onTextboxChangeSignInPassword(e) {
        this.setState({
            signInPassword: e.target.value,
        })
    }
    onTextboxChangeSignUpEmail(e) {
        this.setState({
            signUpEmail: e.target.value,
        })
    }
    onTextboxChangeSignUpPassword(e) {
        this.setState({
            signUpPassword: e.target.value,
        })
    }
    onTextboxChangeSignUpFirstName(e) {
        this.setState({
            signUpFirstName: e.target.value,
        })
    }
    onTextboxChangeSignUpLastName(e) {
        this.setState({
            signUpLastName: e.target.value,
        })
    }

    render() {
        const { isLoading, token, signInError, signInEmail, signInPassword,
        signUpFirstName, signUpLastName, signUpPassword, signUpEmail } = this.state

        if(isLoading) {
            return <div><p>Loading ...</p></div>
        }

        if(!token) {
            return (
                <div>
                    <div>
                        {
                            (signInError) ? (
                                <p>{signInError}</p>
                            ) : (null)
                        }
                        <p>Sign In</p>
                        <input type='email' placeholder='Email' value={signInEmail} />
                        <input type='password' placeholder='Password' value={signInPassword} />
                        <button>Sign In</button>
                    </div>
                    <br/>
                    <br/>
                    <div>
                        <p>Sign Up</p>
                        <input type='text' placeholder='First Name' value={signUpFirstName} />
                        <input type='text' placeholder="Last Name" value={signUpLastName} />
                        <input type='email' placeholder='Email' value={signUpEmail} />
                        <input type='password' placeholder='Password' value={signUpPassword} />
                        <button>Sign Up</button>
                    </div>
                </div>
            )
        }

        return (
            <div>
                <p>Account</p>
            </div>
        )
    }
}
