import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Checkbox from '@material-ui/core/Checkbox';
import FormControlLabel from '@material-ui/core/FormControlLabel';


const useStyles = makeStyles(theme => ({
    container: {
      flexWrap: 'wrap',
    },
    textField: {
      marginLeft: theme.spacing(1),
      marginRight: theme.spacing(1),
    },
    dense: {
      marginTop: theme.spacing(2),
    },
    menu: {
      width: 200,
    },
  }));

export default function ModalOrder(props) {
    const classes = useStyles();
    const [state, setState] = React.useState({
        paye: false,
        recup: false,
        checkedF: true,
      });
    const [values, setValues] = React.useState({
        name: '',
        age: '',
        multiline: '',
        currency: '',
      });

    const handleChange = name => event => {
        setValues({ ...values, [name]: event.target.value });
      };
      
      const handleChangeCheck = name => event => {
        setState({ ...state, [name]: event.target.checked });
        console.log(state)
      };

    return (
        <div style={{minHeight:'50vh'}}>
            <h4 style={{textAlign:'center'}}>Enregistrer une nouvelle commande</h4>
            <br/>
            
            <form className={classes.container} noValidate autoComplete="off">
                <TextField
                    id="outlined-name"
                    label="Numero"
                    className={classes.textField}
                    value={values.name}
                    onChange={handleChange('numero')}
                    margin="normal"
                    variant="outlined"
                />
                <br/>
                <TextField
                    id="outlined-name"
                    label="Montant"
                    className={classes.textField}
                    value={values.name}
                    onChange={handleChange('name')}
                    margin="normal"
                    variant="outlined"
                />
                <br/>
                <TextField
                    id="outlined-name"
                    label="Date de retour"
                    className={classes.textField}
                    value={values.name}
                    onChange={handleChange('name')}
                    margin="normal"
                    variant="outlined"
                />
                <br/>
                <TextField
                    id="outlined-name"
                    label="Infos"
                    className={classes.textField}
                    value={values.name}
                    onChange={handleChange('name')}
                    margin="normal"
                    variant="outlined"
                />
                <br/>
                <FormControlLabel
          value="start"
          control={<Checkbox color="primary" checked={state.paye} onChange={handleChangeCheck('paye')} value="paye" />}
          label="Paye"
          labelPlacement="start"
        />
        <FormControlLabel
          value="start"
          control={<Checkbox color="primary" checked={state.recup} onChange={handleChangeCheck('recup')} value="recup" />}
          label="Recupere"
          labelPlacement="start"
        />
      </form>
        </div>
    )
}
