import axios from 'axios';
import { POST_RESERVATION, FETCH_RESERVATIONS, DELETE_RESERVATION } from './types';


const reservationsAPI = //'http://localhost:8080/api/reservations/' 
                        'https://cleaners-reservation.herokuapp.com/api/reservations/'

export const postReservation = (name, surname, phone, email, address, city, date, hour, info) => dispatch => {
    axios.post(reservationsAPI, {
          name, surname, phone, email, address, city, date, hour, info
      })
      .then(res => {
          return dispatch({
            type: POST_RESERVATION,
            payload: res.data,
          });       
        })
        .catch(err => {
          console.log(err);
          throw new Error('Couldnt post reservation.' + err);
        });
  }
  export const deleteReservationSuccess = id => {
    return {
      type: DELETE_RESERVATION,
      payload: {
        id
      }
    }
  }
  export const deleteReservation = id => {
    return (dispatch) => {
      return axios.get(`${reservationsAPI}/delete/${id}`)
      .then(response => {
        dispatch(deleteReservationSuccess(response.data))
      })
      .catch(err => {
        console.log(err);
        throw new Error('Could not delete reservation.' + err);
      })
    }
  }

  export const fetchReservations = () => dispatch => {

    axios.get(reservationsAPI, {
    })
      .then(res => { 
        let reservations = res.data;
        return dispatch({
          type: FETCH_RESERVATIONS,
          payload: reservations
        });
      })
      .catch(err => {
        console.log(err);
        throw new Error('Could not fetch reservations.' + err);
      });    
  }
