import React, {useState} from 'react';
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { postContact } from '../../store/actions/contactActions';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { FormattedMessage } from 'react-intl'; 




const FormContact = (props) => {
  const [ contact, setContact ] = useState({name: '', phone: '', email: ''})
  console.log(contact)
  
      const handleChange = (e) => {
        setContact ({[e.target.name]: e.target.value})
        }
        
      const handleSubmit = (e) => {
        const textAlert =
        'Votre message a ete envoye nous vous recontacterons dans les plus brefs delais';
        const { name, phone, email } = contact;
        e.preventDefault()
        props.postContact(name, phone, email)
        alert(textAlert);
      }

  
      const name = <FormattedMessage id='contact.name' />;
      const number = <FormattedMessage id='contact.number' />;
      const email = <FormattedMessage id='contact.email' />;
      const message = <FormattedMessage id='contact.message' />;
      

        return (
          <div className="container">
              <form onSubmit={handleSubmit}>            
                  <TextField
                    id="standard-name"
                    label={name}
                    name='name'
                    value={contact.name}
                    onChange={handleChange}
                    margin="normal"
                    required
                  />
                <div className='row'>
                  <div className='col-md-6'>
                    <TextField
                      name="phone"
                      label={number}
                      value={contact.phone}
                      onChange={handleChange}
                      margin="normal"
                    />
                  </div>
                  <div className='col-md-6'>
                    <TextField
                      name="email"
                      label={email}
                      value={contact.email}
                      onChange={handleChange}
                      type="email"
                      autoComplete="email"
                      margin="normal"
                      required
                    />
                  </div>
                </div>                    
                <TextField
                  name="message"
                  label={message}
                  value={contact.message}
                  onChange={handleChange}        
                  placeholder="Message"
                  fullWidth
                  multiline
                  rows="3"
                  margin="normal"
                  required
                />
              <Button variant="outlined" color="primary" type='submit'>
                Submit
              </Button>
            </form>
            </div>
        );
    }
const mapStateToProps = state => ({
    contacts: state.contacts
  })
  FormContact.propTypes = {
    postContact: PropTypes.func.isRequired
  };

export default connect(
    mapStateToProps,{ postContact })(FormContact);
