import React from 'react';
import { Modal, ModalHeader, ModalFooter } from 'reactstrap';
import TabModalCustomer from './TabModalCustomer';
import { deleteCustomer } from '../../../../store/actions/customerActions';
import { connect } from 'react-redux';
import whatsapp from '../../../../images/whatsapp-sw.png';
import plus from '../../../../images/plus-box.png'



 function ModalCustomer(props) {
  const [modal, setModal] = React.useState(false);

    function toggle() {
        setModal(prevState => ({
          modal: !prevState.modal
        }));
      }

        const {customer} = props;
        const closeBtn = <button className="close" onClick={thistoggle}>&times;</button>;
     return(
        <div>
            <li className="list-group-item" onClick={toggle}>{customer.name} {customer.surname} </li>
                <Modal isOpen={modal} toggle={toggle} centered size='lg'  zIndex='10000'>
                    <ModalHeader className="modal-header" style={{backgroundColor:'#42a5f5', color: 'white'}} >
                        {customer.name} {customer.surname}
                    </ModalHeader>
                    <TabModalCustomer customer={customer} />
                    <ModalFooter>
                        <a href={`https://api.whatsapp.com/send?phone=${customer.phone}&text=La commande est prete`} >
                          <img src={whatsapp} alt='wa' />
                        </a>
                        <img src={plus} alt='plus' />
                    </ModalFooter>
            </Modal>
        </div>
     )
    }

