import React, { Component } from 'react';
import { fetchReservations } from '../store/actions/reservationActions';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import NavBar from '../components/NavBar';
import CardReservation from '../components/CardReservation';
import NavAdmin from '../components/NavAdmin';
import SearchDb from '../components/SearchDb';
import ButtonNew from '../components/ButtonNew';
import { Modal, Button, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import Spinner from '../components/Spinner';

class AdminReservation extends Component {
    constructor(props) {
        super(props);
        this.state = {
          modal: false
        };
        this.toggle = this.toggle.bind(this);
    }
    toggle() {
        this.setState(prevState => ({
          modal: !prevState.modal
        }));
      }
    componentDidMount() {
        this.handleFetchReservations();
    }

    handleFetchReservations() {
        this.props.fetchReservations();
        this.setState({loading:false});

}
    
    render() {
        const { reservation } = this.props;
        const r = reservation.map(r => {
            return (
              <CardReservation
              key={r._id}
              reservation={r}
              />
            );
          });

            return (
                <div>
                    <NavBar />
                    <NavAdmin />
                    <SearchDb />
                    <ButtonNew text="Nouveau" onClick={this.toggle}/>
                    <Modal isOpen={this.state.modal} toggle={this.toggle} centered>
                    <ModalHeader className="modal-header" close={closeBtn}>
                        {customer.name} {customer.surname}
                    </ModalHeader>
                    <ModalBody>
                        <h5>Addresse: </h5><p>{customer.address}</p>
                        <h5>Ville: </h5><p>{customer.city}</p>
                        <h5>Tel: </h5><p>{customer.phone}</p>
                        <h5>Email: </h5><p>{customer.email}</p>
                        <h6>informations: </h6><p>{customer.info}</p>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="danger" onClick={e => this.handleDeleteCustomer(e)} >Supprimer</Button>
                    </ModalFooter>
            </Modal>
                    {this.state.loading &&
                     <Spinner />
                    }
                <div className="admin">
                    <ul className="list-group">{r}</ul>
                </div>
                </div>
            )
        }
    }


const mapStateToProps = state => ({
    reservation: state.reservations.reservation
});

AdminReservation.propTypes = {
    fetchReservations: PropTypes.func.isRequired,
}
export default connect(mapStateToProps, { fetchReservations })(AdminReservation)
