import React, {useState, useEffect } from 'react';
import { connect } from 'react-redux';
import {signIn, signOut } from '../../store/actions/googleAuthActions';
import './GoogleAuth.css';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';

export default function GoogleAuth() {

    useEffect(() => {
        window.gapi.load('client:auth2', () => {
            window.gapi.client
            .init({
                clientId: '162564132974-al7mv7822j2b88bqka4ps7q29vn4quk3.apps.googleusercontent.com',
                scope: 'email'
            })
            .then(() => {
                this.auth = window.gapi.auth2.getAuthInstance();
                this.onAuthChange(this.auth.isSignedIn.get());
                this.auth.isSignedIn.listen(this.onAuthChange);
            });
        });
    });

    onAuthChange = isSignedIn => {
        if(isSignedIn) {
            this.props.signIn(this.auth.currentUser.get().getId());
        } else {
            this.props.signOut();
        }
    };

    onSignInClick = () => {
        this.auth.signIn();
    };
    onSignOutClick = () => {
        this.auth.signOut();
        window.location.reload(); 
    };


    renderAuthButton = () => {
        const style= {
            signout: {
                color: 'red',
            },
        }
        if(this.props.isSignedIn === null) {
            return null;
        } else if (this.props.isSignedIn) {
            return (

                <div 
                style={style.signout}
                onClick={this.onSignOutClick}>
                    Sign Out
                </div>
            )
        } else {
            return (
                <div
                className={style.signin}
                onClick={this.onSignInClick}>
                    Sign In
                </div>
            )
        }
    }

    render = () => {
        const [anchorEl, setAnchorEl] = React.useState(null);
        const open = Boolean(anchorEl);
          function handleClose() {
             setAnchorEl(null);
        }
        return (
            <div>
                <Menu
                id="menu-appbar"
                anchorEl={anchorEl}
                anchorOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                keepMounted
                transformOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                open={open}
                onClose={handleClose}
              >
                <MenuItem onClick={handleClose}>{this.renderAuthButton()}</MenuItem>z
              </Menu>
            </div>
        )
    }
}

    const mapStateToProps = (state) => {
    return { isSignedIn: state.auth.isSignedIn };
};
