import React from 'react';
import { deleteCustomer } from '../../../../store/actions/customerActions';
import { Button } from 'reactstrap';
import { connect } from 'react-redux';

function handleDeleteCustomer(e, props) {
    e.preventDefault();
    const id = this.props.customer._id
    props.deleteCustomer(id);
    window.location.reload(); 
  }

function ModalOptions(props) {
    return (
        <div style={{minHeight:'50vh'}}>
            <h5>Options</h5>
            <Button color="danger" onClick={handleDeleteCustomer} >Supprimer</Button>
        </div>
    )
}

export default connect({deleteCustomer})(ModalOptions)
