import React from 'react';
import { TabContent, TabPane, Nav, NavItem, NavLink, Card, Button, CardTitle, CardText, Row, Col, ModalHeader, ModalFooter } from 'reactstrap';
import { FormattedMessage } from 'react-intl'; 

export default class Example extends React.Component {
  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.state = {
      activeTab: 1
    };
  }

  toggle(tab) {
    if (this.state.activeTab !== tab) {
      this.setState({
        activeTab: tab
      });
    }
  }
  handleNext = () => {
    this.setState({
      activeTab: this.state.activeTab + 1
    })
  }
  handlePrev = () => {
    this.setState({
      activeTab: this.state.activeTab - 1
    })
  }
  displayPrev = () => {
    if(this.state.activeTab != 1)
    return (
      <Col xs='6' style={{textAlign:'left'}}>
    <Button onClick={()=> this.handlePrev()}>Precedent</Button>
    </Col>
    )
  }

  render() {
    return (
      <div>
        <TabContent activeTab={this.state.activeTab}>

          <TabPane tabId={1}>
            <ModalHeader>Reserver une livraison</ModalHeader>
            <div className='row justify-content'>
              <div className='col' style={{color:'blue'}}>
                <p><FormattedMessage id="reservation.min" /></p>
                <p><FormattedMessage id="reservation.min2" /></p>
                <p><FormattedMessage id="reservation.min3" /></p>
              </div>
            </div>
          </TabPane>

          <TabPane tabId={2}>
          <ModalHeader>Informations personnelles</ModalHeader>
            <Row>
              <Col sm="12">
                <h4>Tab 2 Contents</h4>
              </Col>
            </Row>
          </TabPane>

          <TabPane tabId={3}>
          <ModalHeader>Addresses</ModalHeader>
            <Row>
              <Col sm="12">
                <h4>Tab 3 Contents</h4>
              </Col>
            </Row>
          </TabPane>

          <TabPane tabId={4}>
          <ModalHeader>Confirmation</ModalHeader>
            <Row>
              <Col sm="6">
                <Card body>
                  <CardTitle>Special Title Treatment</CardTitle>
                  <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
                  <Button>Go somewhere</Button>
                </Card>
              </Col>
              <Col sm="6">
                <Card body>
                  <CardTitle>Special Title Treatment</CardTitle>
                  <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
                </Card>
              </Col>
            </Row>
          </TabPane>

        </TabContent>
        <ModalFooter>
        <Row>
          
            {this.displayPrev()}
          
          <Col xs='6'>
            <Button onClick={()=> this.handleNext()}>Suivant</Button>
          </Col>
        </Row>
        </ModalFooter>
      </div>
    );
  }
}
