import React, {useState} from 'react';
import { Modal, ModalHeader } from 'reactstrap';

export default function ModalOrders(props) {

    const {
        buttonLabel,
        className
      } = props;
    
      const [modal, setModal] = useState(false);
    
      const toggle = () => setModal(!modal);

    const orders = props.customer.orders;    

    function ordersExist() {
        if(orders) {
            return (
                orders.map((o) =>
                <div>
                    <li className='list-group-item' key={o._id}>no <span style={{color:'red'}}>{o.hazmana}</span> || {o.date} || {o.amount} â‚ª, <span style={{color:'blue'}}>{o.payed}</span> </li>
                    <Modal isOpen={modal} toggle={toggle} className={className}>
                    </Modal>
                </div>
                )
            )
        }
        else return <p>Pas de commandes</p>
    }

    return (
        <div style={{minHeight:'50vh'}}>
            <h4 style={{textAlign:'center'}}>Toutes les commandes</h4>
            <br/>
            <div className="admin">
                <ul className="list-group">
                    {ordersExist()}
                </ul>
            </div>
        </div>
    )
}
