import React from 'react';
import { Modal, Button, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import './Commande.css';

 class Commande extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          modal: false,
        };
        this.toggle = this.toggle.bind(this);
    }
    toggle() {
        this.setState(prevState => ({
          modal: !prevState.modal
        }));
      }      

    render() {    
        const {reservation} = this.props;
        const closeBtn = <button className="close" onClick={this.toggle}>&times;</button>;
     return(
        <div>
            <li className="list-group-item" onClick={this.toggle}>{reservation.name} {reservation.surname} </li>
            <Modal isOpen={this.state.modal} toggle={this.toggle} centered>
         <ModalHeader close={closeBtn}>
         <h2>{reservation.name} {reservation.surname}</h2>
         <h5>Addresse: {reservation.address}</h5>
         <h5>Ville: {reservation.city}</h5>
         <h5>Tel: {reservation.phone}</h5>
         <h5>Email: {reservation.email}</h5>
         <h5>Date: {reservation.date}</h5>
         <h5>heure: {reservation.hour}</h5>
         <h7>informations: {reservation.info}</h7>
         </ModalHeader>
            </Modal>
        </div>
     )
    }
 }

export default Commande
