import React from 'react'
import { connect } from 'react-redux'


const handleClickOpen = () => {
    setOpen(true);
  }


function DashboardConfig(props) {
    const [open, setOpen] = React.useState(false);
    const [ value, setValue ] = React.useState('')
    //const [ inputValue, setInputValue ] = React.useState('')

    const handleValue = (e) => {
        setValue(e.target.value)
    }
    const input = () => {
        if(open === true)
        console.log(open)
        return (
            <div>
            <label>{value}</label>
            <input>
            </input>
            </div>
        )
    }
        const listStyle = {
            listStyle: 'none',
        }
        const listElementStyle = {
            paddingTop: '1rem'
        }
        return(
            <div>
                <p>Modify user informations :</p>
                <ul style={listStyle} onClick={handleClickOpen}>
                    <li style={listElementStyle} onClick={handleValue} value='name'>
                        Name
                    </li>
                    {input()}
                    <li style={listElementStyle} onClick={handleValue} value='surname'>
                        Surname
                    </li>
                    {input()}
                    <li style={listElementStyle} onClick={handleValue} value='phone'>
                        Phone 
                    </li>
                    {input()}
                    <li style={listElementStyle} onClick={handleValue} value='address'>
                        Address
                    </li>
                    {input()}
                </ul>
            </div>
        )
    }


const mapStateToProps = state => ({
    auth: state.auth
  });

export default connect(mapStateToProps)(DashboardConfig);
