import React, { Component } from 'react'
import { getFromStorage, setInStorage } from '../../config/storage';
export default class login extends Component {
    constructor(props) {
        super(props);

        this.state = {
            isLoading: true,
            token: '',
            signUpError: '',
            signInError: '',
            signInEmail: '',
            signInPassword: '',
            signUpFirstName: '',
            signUpLastName: '',
            signUpEmail: '',
            signUpPassword: ''
        };
    this.onTextboxChangeSignInEmail = this.onTextboxChangeSignInEmail.bind(this);
    this.onTextboxChangeSignInPassword = this.onTextboxChangeSignInPassword.bind(this);
    this.onTextboxChangeSignUpEmail = this.onTextboxChangeSignUpEmail.bind(this);
    this.onTextboxChangeSignUpPassword = this.onTextboxChangeSignUpPassword.bind(this);
    this.onTextboxChangeSignUpFirstName = this.onTextboxChangeSignUpFirstName.bind(this);
    this.onTextboxChangeSignUpLastName = this.onTextboxChangeSignUpLastName.bind(this);
    
    this.onSignIn = this.onSignIn.bind(this);
    this.onSignUp = this.onSignUp.bind(this);
    this.onSignUp = this.onSignUp.bind(this);
    }
  
    componentDidMount() {
        const obj = getFromStorage('The Cleaners');
        if (obj && obj.token) {
          const { token } = obj;
          // Verify token
          fetch('/api/account/verify?token=' + token)
            .then(res => res.json())
            .then(json => {
              if (json.success) {
                this.setState({
                  token,
                  isLoading: false
                });
              } else {
                this.setState({
                  isLoading: false,
                });
              }
            });
        } else {
          this.setState({
            isLoading: false,
          });
        }
      }

      onTextboxChangeSignInEmail(e) {
          this.setState({
              signInEmail: e.target.value,
          })
      }
      onTextboxChangeSignInPassword(e) {
        this.setState({
            signInPassword: e.target.value,
        })
    }
    onTextboxChangeSignUpEmail(e) {
        this.setState({
            signUpEmail: e.target.value,
        })
    }
    onTextboxChangeSignUpPassword(e) {
        this.setState({
            signUpPassword: e.target.value,
        })
    }
    onTextboxChangeSignUpFirstName(e) {
        this.setState({
            signUpFirstName: e.target.value,
        })
    }
    onTextboxChangeSignUpLastName(e) {
        this.setState({
            signUpLastName: e.target.value,
        })
    }

    onSignIn() {
        // Grab state
        const {
          signInEmail,
          signInPassword,
        } = this.state;
        this.setState({
          isLoading: true,
        });
        // Post request to backend
        fetch('http://localhost:8080/api/signin/account/signin', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            email: signInEmail,
            password: signInPassword,
          }),
        }).then(res => res.json())
          .then(json => {
            console.log('json', json);
            if (json.success) {
              setInStorage('the-cleaners', { token: json.token });
              this.setState({
                signInError: json.message,
                isLoading: false,
                signInPassword: '',
                signInEmail: '',
                token: json.token,
              });
            } else {
              this.setState({
                signInError: json.message,
                isLoading: false,
              });
            }
          });
      }

    onSignUp() {
        // Grab state
        const {
          signUpEmail,
          signUpPassword,
          signUpFirstName,
          signUpLastName
        } = this.state;
        this.setState({
          isLoading: true,
        });
        // Post request to backend
        fetch('http://localhost:8080/api/signin/account/signup', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            email: signUpEmail,
            password: signUpPassword,
            firstName: signUpFirstName,
            lastName: signUpLastName
          }),
        }).then(res => res.json())
          .then(json => {
            console.log('json', json);
            if (json.success) {
              this.setState({
                signUpError: json.message,
                isLoading: false,
                signUpEmail: '',
                signUpPassword: '',
                signUpFirstName: '',
                signUpLastName: ''
              });
            } else {
              this.setState({
                signUpError: json.message,
                isLoading: false,
              });
            }
          });
      }
      
    logout() {
        this.setState({
          isLoading: true,
        });
        const obj = getFromStorage('the_main_app');
        if (obj && obj.token) {
          const { token } = obj;
          // Verify token
          fetch('http://localhost:8080/api/signin/account/verify?token=' + token)
            .then(res => res.json())
            .then(json => {
              if (json.success) {
                this.setState({
                  token: '',
                  isLoading: false
                });
              } else {
                this.setState({
                  isLoading: false,
                });
              }
            });
        } else {
          this.setState({
            isLoading: false,
          });
        }
      }
      

    render() {
        const { isLoading, token, signInError, signUpError, signInEmail, signInPassword,
        signUpFirstName, signUpLastName, signUpPassword, signUpEmail } = this.state

        if(isLoading) {
            return <div><p>Loading ...</p></div>
        }

        if(!token) {
            return (
                <div>
                    <div>
                        {
                            (signInError) ? (
                                <p>{signInError}</p>
                            ) : (null)
                        }
                        <p>Sign In</p>
                        <input type='email' placeholder='Email' value={signInEmail} onChange={this.onTextboxChangeSignInEmail} />
                        <input type='password' placeholder='Password' value={signInPassword} onChange={this.onTextboxChangeSignInPassword} />
                        <button onClick={this.onSignIn}>Sign In</button>
                    </div>
                    <br/>
                    <br/>
                    {
                            (signUpError) ? (
                                <p>{signUpError}</p>
                            ) : (null)
                        }
                    <div>
                        <p>Sign Up</p>
                        <input type='text' placeholder='First Name' value={signUpFirstName} onChange={this.onTextboxChangeSignUpFirstName} />
                        <input type='text' placeholder="Last Name" value={signUpLastName} onChange={this.onTextboxChangeSignUpLastName} />
                        <input type='email' placeholder='Email' value={signUpEmail} onChange={this.onTextboxChangeSignUpEmail} />
                        <input type='password' placeholder='Password' value={signUpPassword} onChange={this.onTextboxChangeSignUpPassword} />
                        <button onClick={this.onSignUp}>Sign Up</button>
                    </div>
                </div>
            )
        }

        return (
            <div>
                <p>Signed in</p>
            </div>
        )
    }
}
