import { LOCALE_SET } from './types';

export const localeSet = lang => ({
    type: LOCALE_SET,
    lang
})

export const setLocale = (lang, props) => dispatch => {
    localStorage.alhubLang = lang;
    dispatch(localeSet(lang));
    if(lang === 'fr') 
        return alert(this,props)
    if(lang ==='he') 
        return alert('heeb')

}
