import React, { Component } from 'react'
import { getFromStorage, setInStorage } from '../../config/storage';
export default class login extends Component {
    constructor(props) {
        super(props);

        this.state = {
            isLoading: true,
            token: '',
            signUpError: '',
            signInError: ''
        };
    }

    componentDidMount() {
        const obj = getFromStorage('the_main_app');
        if (obj && obj.token) {
          const { token } = obj;
          // Verify token
          fetch('/api/account/verify?token=' + token)
            .then(res => res.json())
            .then(json => {
              if (json.success) {
                this.setState({
                  token,
                  isLoading: false
                });
              } else {
                this.setState({
                  isLoading: false,
                });
              }
            });
        } else {
          this.setState({
            isLoading: false,
          });
        }
      }

    render() {
        const { isLoading, token } = this.state

        if(isLoading) {
            return <div><p>Loading ...</p></div>
        }

        if(!token) {
            return (
                <div>
                    <div>
                        <p>Sign In</p>
                        <input type='email' placeholder='Email' />
                        <input type='password' placeholder='Password' />
                    </div>
                    <br/>
                    <br/>
                    <div>
                        <p>Sign Up</p>
                        <input type='email' placeholder='Email' />
                        <input type='password' placeholder='Password' />
                    </div>
                </div>
            )
        }

        return (
            <div>
                <p>Account</p>
            </div>
        )
    }
}
