import axios from 'axios';
import { ON_SIGN_IN, FETCH_RESERVATIONS, DELETE_RESERVATION, MAIL_RESERVATION } from './types';



export const onSignIn = (signInEmail, signInPassword) => dispatch => {
  // Post request to backend
  axios.post('https://www.thecleanersisrael.com/api/account/signin', {
    body: JSON.stringify({
      email: signInEmail,
      password: signInPassword,
    }),
}).then(res => {
    return dispatch({
      type: ON_SIGN_IN,
      payload: res.json,
    });       
  })
  .catch(err => {
    console.log(err);
    throw new Error('Couldnt post contact.' + err);
  });
  }
