import React, {useState} from 'react';
import { Modal, ModalHeader, ModalBody } from 'reactstrap';

export default function ModalOrders(props) {

    const {
        buttonLabel,
        className
      } = props;
    
      const [modal, setModal] = useState(false);
    
      const toggle = () => setModal(!modal);

    const orders = props.customer.orders;    

    function ordersExist() {
        if(orders) {
            return (
                orders.map((o) =>
                <div>
                    <li onClick={toggle} className='list-group-item' key={o._id}>no <span style={{color:'red'}}>{o.hazmana}</span> || {o.date} || {o.amount} â‚ª, <span style={{color:'blue'}}>{o.payed}</span> </li>
                    <Modal centered size='lg' zIndex='10001' isOpen={modal} toggle={toggle} className={className}>
                    <ModalHeader toggle={toggle}>{o.hazmana}</ModalHeader>
                    <ModalBody style={{minHeight:'80vh'}}>
                        Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                    </ModalBody>
                    </Modal>
                </div>
                )
            )
        }
        else return <p>Pas de commandes</p>
    }

    return (
        <div style={{minHeight:'50vh'}}>
            <h4 style={{textAlign:'center'}}>Toutes les commandes</h4>
            <br/>
            <div className="admin">
                <ul className="list-group">
                    {ordersExist()}
                </ul>
            </div>
        </div>
    )
}
