import React, { Component } from 'react'
import { getFromStorage, setInStorage } from '../../config/storage';
export default class login extends Component {
    constructor(props) {
        super(props);

        this.state = {
            isLoading: true,
            token: '',
            signUpError: '',
            signInError: ''
        };
    }

    componentDidMount() {
        const obj = getFromStorage('the_main_app');
        if (obj && obj.token) {
          const { token } = obj;
          // Verify token
          fetch('/api/account/verify?token=' + token)
            .then(res => res.json())
            .then(json => {
              if (json.success) {
                this.setState({
                  token,
                  isLoading: false
                });
              } else {
                this.setState({
                  isLoading: false,
                });
              }
            });
        } else {
          this.setState({
            isLoading: false,
          });
        }
      }

    render() {
        const { isLoading } = this.state
        if(isLoading) {
            return <div><p>Loading ...</p></div>
        }

        const style = {
            fontSize: '30px',
            borderColor: 'black',
            borderStyle: 'solid',
            margin: '10px',
            color: 'black'
          };

        return (
            <div className='container text-center'>
                <div style={style}><a href='http://localhost:8080/api/auth/logout'>Logout</a></div>
                <div style={style}><a href='http://localhost:8080/api/auth/login'>Login</a></div>
                <div style={style}><a href='http://localhost:8080/api/auth/login'>Login with Google</a></div>
            </div>
        )
    }
}
