import React from 'react';
import '../css/HomePage.css';
import logo from '../../images/logo-min.png';
import pressing from '../../images/logo1.png';
import { Link } from 'react-router-dom';
import Grid from '@material-ui/core/Grid';
import { FormattedMessage } from 'react-intl'; 
import banner from '../../images/banner.png';
import Intro from './Intro';

     class HomePage extends React.Component{

        constructor(props) {
            super(props);
            this.state = { imageStatus: "loading" };
          }

        componentWillMount(){
            setTimeout(() => { 
                this.setState({
                    imageStatus: 'loaded'
                })
        }, 2500)
      }

        handleImageLoaded() {
            this.setState({ imageStatus: "loaded" });
          }

          displayContent() {
              if(this.state.imageStatus === 'loading')
              return <div style={{backgroundColor:'red'}}>Loading</div>
              if(this.state.imageStatus === 'loaded')
              return <p>cest bon</p>

          }

         render() {
             console.log(this.state.imageStatus)
            return (
                <div onLoad={this.handleImageLoaded.bind(this)} className='homepage' style={{ backgroundImage:`url(${banner}`, width:'100%', position:'absolute'}}>
                    <div className='container'>
                        <div className='row justify-content'>
                            <div className='text-center'>
                                <img src={logo} width='50%' alt="logoCleaners"></img>
                            </div>
                            <div className='text-center'>
                                <img src={pressing} width='80%' alt="pressing" style={{marginTop:'-25%'}}/>
                            </div>
                            <div className='text-center' style={{color:'white'}}>
                                <p className="lead text-center"><FormattedMessage id="home.intro" defaultMessage=""/></p>
                            </div>
                            <div className= 'col'>
                                <div className='text-center'>
                                    <button className="bouton18">
                                        <Link><p className="text-button"><FormattedMessage id="home.button" defaultMessage="BOOK A DELIVERY"/></p></Link>
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                
        );
    }
}

export default HomePage
