import React from 'react';
import '../css/HomePage.css';
import logo from '../../images/logo-min.png';
import pressing from '../../images/logo1.png';
import ModalReserv from '../../components/modal/ModalReserv';
import { FormattedMessage } from 'react-intl'; 
import Intro from './Intro';

     class HomePage extends React.Component{

        constructor(props) {
            super(props);
            this.state = { imageStatus: "loading" };
          }

        componentDidMount(){
            setTimeout(() => { 
                this.setState({
                    imageStatus: 'loaded'
                })
        }, 2500)
      }

        handleImageLoaded() {
            this.setState({ imageStatus: "loaded" });
          }

          displayContent() {
              if(this.state.imageStatus === 'loading')
                return <Intro />
              if(this.state.imageStatus === 'loaded')
                return (
                    <div className='homepage' style={{ backgroundColor:'#0E1521'}}>
                        <div className='container'>
                            <div className='text-center'>
                            <br/>
                                <img className='text-center' src={logo} width='60%' alt="logoCleaners"></img>
                            </div>
                            <div className='text-center'>
                                <img src={pressing} width='80%' alt="pressing" />
                            </div>
                            <br/>
                        <div className='row justify-content'>
                            <div className='text-center' style={{color:'white'}}>
                                <p className="lead text-center"><FormattedMessage id="home.intro" defaultMessage=""/></p>
                            </div>
                        </div>  
                        <br/>         
                        <div className='text-center'>
                            <ModalReserv />
                        </div>
                    </div>
                </div>

                )

          }

         render() {
            return (
                <div>
                    {this.displayContent()}
                </div>
            )
    }
}

export default HomePage
